<script>
var slides = document.querySelectorAll('.slide');
var currentSlide = 0;
var slideInterval = setInterval(nextSlide,5000);
var slidesMob = document.querySelectorAll('.slideMob');
var currentSlideMob = 0;
var slideInterval = setInterval(nextSlideMob,5000);

function nextSlide() {
   slides[currentSlide].className = 'slide';
   currentSlide = (currentSlide+1)%slides.length;
   slides[currentSlide].className = 'slide showing';
}

function nextSlideMob() {
   slidesMob[currentSlideMob].className = 'slideMob';
   currentSlideMob = (currentSlideMob+1)%slidesMob.length;
   slidesMob[currentSlideMob].className = 'slideMob showing';
}
</script>
