<style>
    @media screen and (max-width: 1500px) {
        #navPages-4 { right:0; }
    }
</style>

<script>
    function handleClick(event) {
        event.preventDefault();
        if (window.innerWidth > 1400) {
            const href = event.target.getAttribute('href');
            window.location.href = href;
        }
    }
    {{#if (lang (concat (concat 'custom.header.mega-menu.category-'(getVar 'index'))'.view')) '===' "true"}}
    function startSlider{{getVar 'index'}}(){

        const index = "{{getVar 'index'}}";
        const mainClass = ".nav-slider";
        let slideCount = $(`${mainClass}[index="${index}"] ul li`).length;
        let slideWidth = $(`${mainClass}[index="${index}"] ul li`).width();
        let slideHeight = $(`${mainClass}[index="${index}"] ul li`).height();
        // let slideWidth = 320;
        // let slideHeight = 500;
        let sliderUlWidth = slideCount * slideWidth;

        $(`${mainClass}[index="${index}"]`).css(
             { width: slideWidth, height: slideHeight }
            );

        $(`${mainClass}[index="${index}"] ul`).css({ width: sliderUlWidth, marginLeft: - slideWidth });

        $(`${mainClass}[index="${index}"] ul li:last-child`).prependTo(`${mainClass}[index="${index}"] ul`);

        function moveRight() {
            $(`${mainClass}[index="${index}"] ul`).animate({
                opacity: "10%"
            }, 200, function () {
                $(`${mainClass}[index="${index}"] ul li:first-child`).appendTo(`${mainClass}[index="${index}"] ul`);
                $(`${mainClass}[index="${index}"] ul`).css('left', '');
            });
            $(`${mainClass}[index="${index}"] ul`).animate({
                opacity: "100%"
            }, 200, function(){});
        };
        let notStarted{{getVar 'index'}} = true;
        $(`.navPages-item[index='{{getVar 'index'}}']`).on( "mouseenter", function(){
            if(notStarted{{getVar 'index'}}){
                setInterval(function () {
                    if($("#navPages-{{getVar 'index'}}").css("display") !== "none" && screen.width > 1024){
                       moveRight(); 
                    }

                }, 7000);
                notStarted{{getVar 'index'}} = false;
            }else{
                return;
            }
        } );

    }
    /*
    let notStarted{{getVar 'index'}} = true;
    $(`.navPages-item[index='{{getVar 'index'}}']`).on( "mouseenter", function(){
        setTimeout(function(){
            if(notStarted{{getVar 'index'}}){
                startSlider{{getVar 'index'}}();
                notStarted{{getVar 'index'}} = false;
            }else{
                return;
            }

        }, 0);
    } ); 
    */
    // let notStarted{{getVar 'index'}} = true;
    $(document).ready(function(){
        setTimeout(function(){
            startSlider{{getVar 'index'}}();
        }, 0);
    } );

    {{/if}}


</script>

components/common/paginator:
{{#and
    (if show_dropdown)
    (if (length links) '>' 1)
}}
    {{#or (if page_type 'category') (if page_type 'search')}}
        {{> components/custom/paginator-dropdown page_count=paginator_dropdown_page_count}}
    {{else}}
        {{> components/custom/paginator-dropdown page_count=(get 'number' (last links))}}
    {{/or}}
{{else}}
    {{#or (if (length links) '>' 1) (if next) (if prev)}}
        <nav class="pagination" aria-label="pagination">
            <ul class="pagination-list">
                {{#if previous}}
                    <li class="pagination-item pagination-item--previous">
                        <a class="pagination-link"
                        href="{{previous}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.previous'}}"
                        >
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-left"></use>
                                </svg>
                            </i>
                            {{lang 'common.previous'}}
                        </a>
                    </li>
                {{/if}}

                {{#each links}}
                    <li class="pagination-item {{#if this.number '==' ../current}}pagination-item--current{{/if}}">
                        <a class="pagination-link"
                        href="{{this.url}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        {{#if this.number '==' ../current}}
                        aria-current="page"
                        data-pagination-current-page-link
                        {{/if}}
                        aria-label="{{lang 'common.paginator.page_of' current=this.number total=../links.length}}"
                        >
                            {{this.number}}
                        </a>
                    </li>
                {{/each}}

                {{#if next}}
                    <li class="pagination-item pagination-item--next">
                        <a class="pagination-link"
                        href="{{next}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.next'}}"
                        >
                            {{lang 'common.next'}}
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-right"></use>
                                </svg>
                            </i>
                        </a>
                    </li>
                {{/if}}
            </ul>
        </nav>
    {{/or}}
{{/and}}

