    let notStarted{{getVar 'index'}} = true;
    $(`.navPages-item[index='{{getVar 'index'}}']`).on( "mouseenter", function(){
        setTimeout(function(){
            if(notStarted{{getVar 'index'}}){
                startSlider{{getVar 'index'}}();
                notStarted{{getVar 'index'}} = false;
            }else{
                return;
            }

        }, 0);
    } ); 
    */
    // let notStarted{{getVar 'index'}} = true;
    $(document).ready(function(){
        setTimeout(function(){
            startSlider{{getVar 'index'}}();
        }, 0);
