<script>
        (function () {
          // const $slideSetContent = document.querySelector('[set-content-slide][type="pc"][blog-type="sku"]');
          const $slideSetContentProduct = document.querySelector('[set-content-slide][type="pc"][blog-type="sku-product"]');
          // $slideSetContent.style.display = "none";
          // $slideSetContentProduct.style.display = "none";
          let sku = document.querySelector('#sku');
          const arrMob = [];
          let count = 0;
          let temp = "";
          if(sku === undefined || sku === null){
            document.querySelector(".blog-post__sku-item-container").style.display = "none";
          }
          else if(sku.textContent.length > 0){

            let sku_list = sku.textContent.replace(/[^0-9a-zA-Z,-]/g, '').split(",");
            sku_list = sku_list.filter(s => s.length > 0);
            // console.log(sku_list);

            sku_list.forEach((item)=>{

                const template = `
                <a class="blog-post__sku-item" id="sku_${item}">
                    <div class="blog-skeleton image-skeleton blog-post__sku-image"></div>
                    <img class="blog-post__sku-image" src="">
                    <h3 class="blog-post__sku-title"></h3>
                    <p class="blog-post__sku-number">SKU: ${item}</p>
                </a>`;

                $slideSetContentProduct.innerHTML +=  
                `<div data-product-slide class='productCarousel-slide product-${item}'>

                </div>`

            });

        }else{
            document.querySelector(".blog-post__sku-item-container").style.display = "none";
        }
    })();

</script>
